import React, { createContext, useContext, useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const SocketContext = createContext(null);

export const useSocket = () => {
  const context = useContext(SocketContext);
  if (!context) {
    throw new Error('useSocket must be used within a SocketProvider');
  }
  return context;
};

export const SocketProvider = ({ children }) => {
  const [isConnected, setIsConnected] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const socketRef = useRef(null);
  const eventListenersRef = useRef(new Map());

  // Initialize socket connection
  useEffect(() => {
    const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;
    console.log('ðŸ”Œ Initializing WebSocket connection to:', API_BASE_URL);
    
    socketRef.current = io(API_BASE_URL, {
      transports: ['websocket', 'polling'],
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 10000,
      secure: true // Use secure connection for HTTPS
    });

    // Connection events
    socketRef.current.on('connect', () => {
      console.log('âœ… Connected to server:', socketRef.current.id);
      setIsConnected(true);
      addNotification('Connected to real-time updates', 'success');
    });

    socketRef.current.on('disconnect', (reason) => {
      console.log('âŒ Disconnected from server:', reason);
      setIsConnected(false);
      addNotification('Disconnected from real-time updates', 'warning');
    });

    socketRef.current.on('connect_error', (error) => {
      console.error('Connection error:', error);
      setIsConnected(false);
      addNotification('Connection error: ' + error.message, 'error');
    });

    socketRef.current.on('reconnect_attempt', (attempt) => {
      console.log(`ðŸ”„ Reconnection attempt ${attempt}`);
    });

    socketRef.current.on('reconnect_failed', () => {
      console.error('Reconnection failed');
      addNotification('Failed to reconnect to server', 'error');
    });

    // Cleanup on unmount
    return () => {
      console.log('ðŸ”Œ Disconnecting socket...');
      if (socketRef.current) {
        socketRef.current.disconnect();
        socketRef.current = null;
      }
    };
  }, []);

  // Add notification
  const addNotification = (message, type = 'info') => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, message, type }]);
    
    setTimeout(() => {
      setNotifications(prev => prev.filter(notif => notif.id !== id));
    }, 5000);
  };

  // Remove notification
  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(notif => notif.id !== id));
  };

  // Emit event
  const emit = (event, data) => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit(event, data);
    } else {
      console.warn('âš ï¸ Cannot emit event - socket not connected');
    }
  };

  // Listen to event
  const on = (event, callback) => {
    if (socketRef.current) {
      socketRef.current.on(event, callback);
      
      // Store listener for cleanup
      if (!eventListenersRef.current.has(event)) {
        eventListenersRef.current.set(event, []);
      }
      eventListenersRef.current.get(event).push(callback);
    }
  };

  // Remove listener
  const off = (event, callback) => {
    if (socketRef.current) {
      socketRef.current.off(event, callback);
    }
  };

  const value = {
    isConnected,
    notifications,
    addNotification,
    removeNotification,
    emit,
    on,
    off
  };

  return (
    <SocketContext.Provider value={value}>
      {children}
    </SocketContext.Provider>
  );
};
