import React, { useState, useEffect } from 'react';
import { useAnnouncements } from '../hooks/useAnnouncements.js';

const Announcements = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const { announcements, loading, error, isConnected } = useAnnouncements();

  // Auto-slide effect
  useEffect(() => {
    if (announcements.length > 0) {
      const interval = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % announcements.length);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [announcements.length]);

  // FIXED: Function to get image URL - handles both string and object formats
  const getImageUrl = (announcement) => {
    if (!announcement) return '';
    
    console.log('ðŸ” Checking announcement image structure:', {
      id: announcement._id,
      hasImageUrl: !!announcement.imageUrl,
      hasImageField: !!announcement.image,
      imageType: typeof announcement.image,
      imageStructure: announcement.image
    });

    // Priority 1: Use imageUrl from server (complete data URL)
    if (announcement.imageUrl && typeof announcement.imageUrl === 'string') {
      console.log(`ðŸ–¼ï¸ Using imageUrl for ${announcement._id}`);
      return announcement.imageUrl;
    }
    
    // Priority 2: Handle string format (YOUR ACTUAL DATA FORMAT)
    if (announcement.image && typeof announcement.image === 'string') {
      console.log(`ðŸ“ Using string image format for ${announcement._id}`);
      const base64Data = announcement.image;
      
      // Check if it's already a data URL
      if (base64Data.startsWith('data:image/')) {
        return base64Data;
      }
      
      // It's raw base64 - convert to data URL
      // Detect image type from base64 signature
      let contentType = 'image/jpeg'; // default
      
      if (base64Data.startsWith('iVBORw0KGgo')) {
        contentType = 'image/png';
      } else if (base64Data.startsWith('/9j/')) {
        contentType = 'image/jpeg';
      } else if (base64Data.startsWith('R0lGODlh')) {
        contentType = 'image/gif';
      }
      
      console.log(`ðŸ”„ Converting base64 to ${contentType} data URL`);
      return `data:${contentType};base64,${base64Data}`;
    }
    
    // Priority 3: Handle nested image object (server schema format)
    if (announcement.image && announcement.image.data) {
      console.log(`ðŸ“ Using nested image object for ${announcement._id}`);
      const imageData = announcement.image.data;
      const contentType = announcement.image.contentType || 'image/jpeg';
      
      if (imageData.startsWith('data:image/')) {
        return imageData;
      }
      return `data:${contentType};base64,${imageData}`;
    }
    
    console.warn(`âŒ No valid image data found for announcement: ${announcement._id}`);
    return '';
  };

  const getPlaceholderUrl = () => {
    return `${process.env.PUBLIC_URL}/image/placeholder.jpg`;
  };

  const handleImageError = (e, announcement) => {
    console.warn(`âŒ Image failed to load for announcement: ${announcement._id}`, {
      srcAttempted: e.target.src.substring(0, 100),
      imageStructure: announcement.image
    });
    
    // Set placeholder
    if (e.target) {
      e.target.src = getPlaceholderUrl();
      e.target.onerror = null; // Prevent infinite loop
    }
  };

  const showSlide = (index) => {
    setCurrentSlide(index);
  };

  // Debug: Log all announcements when they load
  useEffect(() => {
    if (announcements.length > 0) {
      console.log('ðŸ“‹ ALL ANNOUNCEMENTS DATA:', announcements.map(ann => ({
        id: ann._id,
        title: ann.title,
        imageType: typeof ann.image,
        imageStructure: ann.image,
        imageUrl: ann.imageUrl
      })));
    }
  }, [announcements]);

  if (loading) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="loading">Loading announcements...</div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="error-message">
            Error: {error}
            <button 
              onClick={() => window.location.reload()} 
              className="retry-button"
            >
              Retry
            </button>
          </div>
        </div>
      </section>
    );
  }

  if (announcements.length === 0) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="no-announcements">
            No announcements available.
            {!isConnected && <div className="connection-note">(Real-time updates offline)</div>}
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="announcements" className="section announcements">
      <div className="container">
        <h2 className="section-title">Recent <span>Announcements</span></h2>
        
        <div className="announcement-slider">
          <div className="announcement-track">
            {announcements.map((announcement, index) => {
              const imageUrl = getImageUrl(announcement);
              const hasImage = !!imageUrl;
              
              console.log(`ðŸŽ¯ Slide ${index}:`, {
                id: announcement._id,
                title: announcement.title,
                hasImage: hasImage,
                imageType: typeof announcement.image,
                imageUrlPreview: imageUrl ? imageUrl.substring(0, 100) + '...' : 'No image'
              });

              return (
                <div 
                  key={announcement._id} 
                  className="announcement-slide" 
                  style={{ display: index === currentSlide ? 'flex' : 'none' }}
                >
                  <div className="announcement-info">
                    <h3 className="announcement-title">{announcement.title}</h3>
                    <p className="announcement-date">{announcement.date}</p>
                    <p className="announcement-desc">{announcement.description}</p>
                  </div>
                  <div className="announcement-img">
                    {hasImage ? (
                      <img 
                        src={imageUrl}
                        alt={announcement.title}
                        onError={(e) => handleImageError(e, announcement)}
                        onLoad={() => console.log(`âœ… Image loaded for: ${announcement.title}`)}
                        style={{ 
                          maxWidth: '100%',
                          height: 'auto'
                        }}
                        crossOrigin="anonymous"
                      />
                    ) : (
                      <div className="no-image-placeholder">
                        <span>No Image Available</span>
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
          
          <div className="slider-nav">
            {announcements.map((_, index) => (
              <div 
                key={index}
                className={`slider-dot ${index === currentSlide ? 'active' : ''}`}
                onClick={() => showSlide(index)}
              ></div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Announcements;
