import React from 'react';
import './DownloadComponent.css';

const DownloadComponent = () => {
    // Your GitHub release information
    const releaseInfo = {
        directUrl: "https://github.com/Thugtwist/apptest/releases/download/v1.0.0/IBMMSLapk",
        githubPage: "https://github.com/Thugtwist/apptest/releases/tag/v1.0.0",
        fileName: "IBMMSL.apk",
        fileSize: "95.2 MB",
        version: "v1.0.0",
        releaseDate: "October 2024"
    };

    // Handle download click
    const handleDownload = () => {
        // Optional: Add download tracking
        console.log('Download started:', releaseInfo.fileName);
        
        // The actual download happens via the link
        // You can add analytics here if needed
    };

    // Handle GitHub page click
    const handleGitHubClick = () => {
        window.open(releaseInfo.githubPage, '_blank');
    };

    return (
        <div className="download-component">
            <div className="download-card">
                <div className="app-icon">
                    üì±
                </div>
                
                <h2 className="app-title">My Awesome App</h2>
                <p className="app-description">Production Release - Ready to Install</p>
                
                <div className="version-info">
                    <span className="version-badge">Version {releaseInfo.version}</span>
                    <span className="size-badge">{releaseInfo.fileSize}</span>
                </div>

                <div className="download-buttons">
                    {/* Instant Download Button */}
                    <a 
                        href={releaseInfo.directUrl}
                        download={releaseInfo.fileName}
                        className="download-btn primary"
                        onClick={handleDownload}
                    >
                        <span className="btn-icon">‚¨áÔ∏è</span>
                        Instant Download APK
                    </a>

                    {/* GitHub Release Page */}
                    <button 
                        className="download-btn secondary"
                        onClick={handleGitHubClick}
                    >
                        <span className="btn-icon">üîó</span>
                        View on GitHub
                    </button>
                </div>

                <div className="download-info">
                    <div className="info-item">
                        <strong>File:</strong> {releaseInfo.fileName}
                    </div>
                    <div className="info-item">
                        <strong>Size:</strong> {releaseInfo.fileSize}
                    </div>
                    <div className="info-item">
                        <strong>Version:</strong> {releaseInfo.version}
                    </div>
                    <div className="info-item">
                        <strong>Released:</strong> {releaseInfo.releaseDate}
                    </div>
                </div>

                {/* Installation Instructions */}
                <div className="instructions">
                    <h4>üìã Installation Instructions:</h4>
                    <ol>
                        <li>Click "Instant Download APK" above</li>
                        <li>Open the downloaded file</li>
                        <li>Allow "Install from unknown sources" if prompted</li>
                        <li>Follow installation steps</li>
                        <li>Launch and enjoy!</li>
                    </ol>
                </div>
            </div>
        </div>
    );
};

export default DownloadComponent;
