import React, { useState, useEffect } from 'react';
import { useAnnouncements } from '../hooks/useApi';
import { useSocket } from '../hooks/useSocket';

const Announcements = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const { loading, error, getActiveAnnouncements } = useAnnouncements();
  const { socketService, isConnected } = useSocket();
  const [announcements, setAnnouncements] = useState([]);

  // Load announcements and setup WebSocket
  useEffect(() => {
    loadAnnouncements();
    setupWebSocket();

    return () => {
      // Cleanup socket listeners
      socketService.off('announcement_created');
      socketService.off('announcement_updated');
      socketService.off('announcement_deleted');
    };
  }, []);

  const loadAnnouncements = async () => {
    try {
      const response = await getActiveAnnouncements();
      if (response.success) {
        setAnnouncements(response.data);
      }
    } catch (err) {
      console.error('Failed to load announcements:', err);
    }
  };

  const setupWebSocket = () => {
    socketService.joinAnnouncements();

    socketService.onAnnouncementCreated((newAnnouncement) => {
      setAnnouncements(prev => [newAnnouncement, ...prev]);
    });

    socketService.onAnnouncementUpdated((updatedAnnouncement) => {
      setAnnouncements(prev => 
        prev.map(ann => 
          ann._id === updatedAnnouncement._id ? updatedAnnouncement : ann
        )
      );
    });

    socketService.onAnnouncementDeleted(({ id }) => {
      setAnnouncements(prev => prev.filter(ann => ann._id !== id));
    });
  };

  // Auto-slide effect
  useEffect(() => {
    if (announcements.length > 0) {
      const interval = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % announcements.length);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [announcements.length]);

  // Simplified image URL handling - server now provides full URLs
  const getImageUrl = (announcement) => {
    if (!announcement) return '';
    
    // Server now provides full image URLs
    if (announcement.image && typeof announcement.image === 'string') {
      return announcement.image;
    }
    
    console.warn(`❌ No valid image data found for announcement: ${announcement._id}`);
    return '';
  };

  const getPlaceholderUrl = () => {
    return `${process.env.PUBLIC_URL}/images/placeholder.jpg`;
  };

  const handleImageError = (e, announcement) => {
    console.warn(`❌ Image failed to load for announcement: ${announcement._id}`);
    
    // Set placeholder
    if (e.target) {
      e.target.src = getPlaceholderUrl();
      e.target.onerror = null; // Prevent infinite loop
    }
  };

  const showSlide = (index) => {
    setCurrentSlide(index);
  };

  if (loading) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="loading">Loading announcements...</div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="error-message">
            Error: {error}
            <button 
              onClick={() => window.location.reload()} 
              className="retry-button"
            >
              Retry
            </button>
          </div>
        </div>
      </section>
    );
  }

  if (announcements.length === 0) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="no-announcements">
            No announcements available.
            {!isConnected && <div className="connection-note">(Real-time updates offline)</div>}
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="announcements" className="section announcements">
      <div className="container">
        <h2 className="section-title">Recent <span>Announcements</span></h2>
        
        <div className="announcement-slider">
          <div className="announcement-track">
            {announcements.map((announcement, index) => {
              const imageUrl = getImageUrl(announcement);
              const hasImage = !!imageUrl;
              
              return (
                <div 
                  key={announcement._id} 
                  className="announcement-slide" 
                  style={{ display: index === currentSlide ? 'flex' : 'none' }}
                >
                  <div className="announcement-info">
                    <h3 className="announcement-title">{announcement.title}</h3>
                    <p className="announcement-date">{announcement.date}</p>
                    <p className="announcement-desc">{announcement.description}</p>
                  </div>
                  <div className="announcement-img">
                    {hasImage ? (
                      <img 
                        src={imageUrl}
                        alt={announcement.title}
                        onError={(e) => handleImageError(e, announcement)}
                        style={{ 
                          maxWidth: '100%',
                          height: 'auto'
                        }}
                        crossOrigin="anonymous"
                      />
                    ) : (
                      <div className="no-image-placeholder">
                        <span>No Image Available</span>
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
          
          <div className="slider-nav">
            {announcements.map((_, index) => (
              <div 
                key={index}
                className={`slider-dot ${index === currentSlide ? 'active' : ''}`}
                onClick={() => showSlide(index)}
              ></div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Announcements;
