import { useState, useEffect } from 'react';
import { apiService } from '../services/apiService';

export const useApi = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const callApi = async (apiCall, ...args) => {
    setLoading(true);
    setError(null);
    
    try {
      const result = await apiCall(...args);
      return result;
    } catch (err) {
      const errorMessage = err.message || 'An unexpected error occurred';
      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const clearError = () => setError(null);

  return {
    loading,
    error,
    callApi,
    clearError
  };
};

// Specific hooks for different features
export const useAnnouncements = () => {
  const { loading, error, callApi, clearError } = useApi();

  const getAnnouncements = (page = 1, limit = 10) => 
    callApi(apiService.getAnnouncements, page, limit);

  const getActiveAnnouncements = () => 
    callApi(apiService.getActiveAnnouncements);

  const createAnnouncement = (formData) => 
    callApi(apiService.createAnnouncement, formData);

  const updateAnnouncement = (id, formData) => 
    callApi(apiService.updateAnnouncement, id, formData);

  const deleteAnnouncement = (id) => 
    callApi(apiService.deleteAnnouncement, id);

  return {
    loading,
    error,
    clearError,
    getAnnouncements,
    getActiveAnnouncements,
    createAnnouncement,
    updateAnnouncement,
    deleteAnnouncement
  };
};

export const useInquiries = () => {
  const { loading, error, callApi, clearError } = useApi();

  const submitInquiry = (inquiryData) => 
    callApi(apiService.submitInquiry, inquiryData);

  const getInquiries = (page = 1, limit = 10) => 
    callApi(apiService.getInquiries, page, limit);

  const updateInquiryStatus = (id, status) => 
    callApi(apiService.updateInquiryStatus, id, status);

  return {
    loading,
    error,
    clearError,
    submitInquiry,
    getInquiries,
    updateInquiryStatus
  };
};

export const useReviews = () => {
  const { loading, error, callApi, clearError } = useApi();

  const getReviews = () => 
    callApi(apiService.getReviews);

  const getApprovedReviews = () => 
    callApi(apiService.getApprovedReviews);

  const submitReview = (reviewData) => 
    callApi(apiService.submitReview, reviewData);

  const updateReview = (id, reviewData) => 
    callApi(apiService.updateReview, id, reviewData);

  const deleteReview = (id) => 
    callApi(apiService.deleteReview, id);

  return {
    loading,
    error,
    clearError,
    getReviews,
    getApprovedReviews,
    submitReview,
    updateReview,
    deleteReview
  };
};

export const useSchools = () => {
  const { loading, error, callApi, clearError } = useApi();

  const getSchools = () => 
    callApi(apiService.getSchools);

  const getActiveSchools = () => 
    callApi(apiService.getActiveSchools);

  const createSchool = (formData) => 
    callApi(apiService.createSchool, formData);

  const updateSchool = (id, formData) => 
    callApi(apiService.updateSchool, id, formData);

  const deleteSchool = (id) => 
    callApi(apiService.deleteSchool, id);

  return {
    loading,
    error,
    clearError,
    getSchools,
    getActiveSchools,
    createSchool,
    updateSchool,
    deleteSchool
  };
};
