import { API_CONFIG } from './config';

class ApiService {
  constructor() {
    this.baseURL = API_CONFIG.BASE_URL;
  }

  // Generic request method with error handling
  async request(endpoint, options = {}) {
    const url = `${this.baseURL}${endpoint}`;
    
    const defaultHeaders = {
      'Content-Type': 'application/json',
    };

    const config = {
      headers: {
        ...defaultHeaders,
        ...options.headers,
      },
      ...options,
    };

    // Remove Content-Type for FormData (let browser set it)
    if (options.body instanceof FormData) {
      delete config.headers['Content-Type'];
    }

    try {
      const response = await fetch(url, config);
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
      }
      
      return await response.json();
    } catch (error) {
      console.error('API request failed:', error);
      throw error;
    }
  }

  // ========== INQUIRIES ==========
  async submitInquiry(inquiryData) {
    return this.request(API_CONFIG.ENDPOINTS.INQUIRIES, {
      method: 'POST',
      body: JSON.stringify(inquiryData)
    });
  }

  async getInquiries(page = 1, limit = 10) {
    return this.request(`${API_CONFIG.ENDPOINTS.INQUIRIES}?page=${page}&limit=${limit}`);
  }

  async updateInquiryStatus(id, status) {
    return this.request(`${API_CONFIG.ENDPOINTS.INQUIRIES}/${id}/status`, {
      method: 'PATCH',
      body: JSON.stringify({ status })
    });
  }

  // ========== ANNOUNCEMENTS ==========
  async getAnnouncements(page = 1, limit = 10) {
    return this.request(`${API_CONFIG.ENDPOINTS.ANNOUNCEMENTS}?page=${page}&limit=${limit}`);
  }

  async getActiveAnnouncements() {
    return this.request(`${API_CONFIG.ENDPOINTS.ANNOUNCEMENTS}/active`);
  }

  async getAnnouncement(id) {
    return this.request(`${API_CONFIG.ENDPOINTS.ANNOUNCEMENTS}/${id}`);
  }

  async createAnnouncement(formData) {
    return this.request(API_CONFIG.ENDPOINTS.ANNOUNCEMENTS, {
      method: 'POST',
      body: formData
    });
  }

  async updateAnnouncement(id, formData) {
    return this.request(`${API_CONFIG.ENDPOINTS.ANNOUNCEMENTS}/${id}`, {
      method: 'PUT',
      body: formData
    });
  }

  async deleteAnnouncement(id) {
    return this.request(`${API_CONFIG.ENDPOINTS.ANNOUNCEMENTS}/${id}`, {
      method: 'DELETE'
    });
  }

  // ========== SCHOOLS ==========
  async getSchools() {
    return this.request(API_CONFIG.ENDPOINTS.SCHOOLS);
  }

  async getActiveSchools() {
    return this.request(`${API_CONFIG.ENDPOINTS.SCHOOLS}/active`);
  }

  async getSchool(id) {
    return this.request(`${API_CONFIG.ENDPOINTS.SCHOOLS}/${id}`);
  }

  async createSchool(formData) {
    return this.request(API_CONFIG.ENDPOINTS.SCHOOLS, {
      method: 'POST',
      body: formData
    });
  }

  async updateSchool(id, formData) {
    return this.request(`${API_CONFIG.ENDPOINTS.SCHOOLS}/${id}`, {
      method: 'PUT',
      body: formData
    });
  }

  async deleteSchool(id) {
    return this.request(`${API_CONFIG.ENDPOINTS.SCHOOLS}/${id}`, {
      method: 'DELETE'
    });
  }

  // ========== REVIEWS ==========
  async getReviews() {
    return this.request(API_CONFIG.ENDPOINTS.REVIEWS);
  }

  async getApprovedReviews() {
    return this.request(`${API_CONFIG.ENDPOINTS.REVIEWS}/approved`);
  }

  async getReview(id) {
    return this.request(`${API_CONFIG.ENDPOINTS.REVIEWS}/${id}`);
  }

  async submitReview(reviewData) {
    return this.request(API_CONFIG.ENDPOINTS.REVIEWS, {
      method: 'POST',
      body: JSON.stringify(reviewData)
    });
  }

  async updateReview(id, reviewData) {
    return this.request(`${API_CONFIG.ENDPOINTS.REVIEWS}/${id}`, {
      method: 'PUT',
      body: JSON.stringify(reviewData)
    });
  }

  async deleteReview(id) {
    return this.request(`${API_CONFIG.ENDPOINTS.REVIEWS}/${id}`, {
      method: 'DELETE'
    });
  }

  // ========== SYSTEM ==========
  async healthCheck() {
    return this.request(API_CONFIG.ENDPOINTS.HEALTH);
  }
}

// Create and export singleton instance
export const apiService = new ApiService();
export default apiService;
